package Circle;public class Circle {    private double radius;    private  static  int onOfObjecrs;    public Circle(){        radius =1;        onOfObjecrs++;    }    public Circle(double radius){        this.radius = radius;        onOfObjecrs++;    }    public double getRadius(){        return radius;    }    public void setRadius(double radius){        this.radius = radius;    }    public static  int getOnOfObjecrs(){        return onOfObjecrs;    }    public double getArea(){        return radius * radius * Math.PI;    }}